import React from "react";
import { useState } from "react";

export function useValidation() {
  const [error, setError] = useState(null);
  const validate = (event) => {
    if (event.target.value.length === 0) {
      setError("Field cannot be empty pal");
    } else {
      setError(null);
    }
  };
  const ErrorDisplay = () => {
    if (error) {
      return <div className="error">{error}</div>;
    }
    return null;
  };
  return [ErrorDisplay, validate];
}

export function withFieldValidation(WrappedComponent) {
  return class extends React.Component {
    constructor(props) {
      super(props);
      this.state = { error: null };
    }

    validate(name, value) {
      if (value.length === 0) {
        this.setState({ error: "Field cannot be empty" });
      } else {
        this.setState({ error: null });
      }
      this.props.onUpdate(name, value);
    }

    render() {
      const { error } = this.state;
      const { onUpdate, ...passThroughProps } = this.props;
      return (
        <div className="Validated">
          <WrappedComponent
            onUpdate={this.validate.bind(this)}
            {...passThroughProps}
          />
          {error && <div className="Error">{error}</div>}
        </div>
      );
    }
  };
}
